import { useState } from 'react';
import {
  Box,
  Typography,
  Hidden,
  Container,
  Button,
  Grid
} from '@mui/material';
import TextField from '@mui/material/TextField';
import { Helmet } from 'react-helmet-async';
import RefreshTwoToneIcon from '@mui/icons-material/RefreshTwoTone';
import LoadingButton from '@mui/lab/LoadingButton';

import { styled } from '@mui/material/styles';

const GridWrapper = styled(Grid)(
  ({ theme }) => `
    background: ${theme.colors.gradients.black1};
`
);

const MainContent = styled(Box)(
  () => `
    height: 100%;
    display: flex;
    flex: 1;
    overflow: auto;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`
);

const TypographyPrimary = styled(Typography)(
  ({ theme }) => `
      color: ${theme.colors.alpha.white[100]};
`
);

const TypographySecondary = styled(Typography)(
  ({ theme }) => `
      color: ${theme.colors.alpha.white[70]};
`
);

const divStyle = {
  backgroundImage: 'url(/static/images/bg5-4.jpg)',
  minheight: '100vh',
  width: '100%',
  backgroundRepeat: 'no-repeat',
  backgroundPosition: 'center center',
  backgroundSize: 'cover',
  padding: '40px'


};


function Status500() {

  const [pending, setPending] = useState(false);
  function handleClick() {
    setPending(true);
  }

  const classes = useStyles();
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    if (state.username.trim() && state.password.trim()) {
     dispatch({
       type: 'setIsButtonDisabled',
       payload: false
     });
    } else {
      dispatch({
        type: 'setIsButtonDisabled',
        payload: true
      });
    }
  }, [state.username, state.password]);

  const handleLogin = () => {
    if (state.username === 'admin@gmail.com' && state.password === 'admin') {
      dispatch({
        type: 'loginSuccess',
        payload: 'Login Successfully'
      });
    } else {
      dispatch({
        type: 'loginFailed',
        payload: 'Incorrect username or password'
      });
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.keyCode === 13 || event.which === 13) {
      state.isButtonDisabled || handleLogin();
    }
  };

  const handleUsernameChange: React.ChangeEventHandler<HTMLInputElement> =
    (event) => {
      dispatch({
        type: 'setUsername',
        payload: event.target.value
      });
    };

  const handlePasswordChange: React.ChangeEventHandler<HTMLInputElement> =
    (event) => {
      dispatch({
        type: 'setPassword',
        payload: event.target.value
      });
    }

  return (
    <>
      <Helmet>
        <title>Inabia - ediscovery</title>
      </Helmet>
      <form className={classes.container} noValidate autoComplete="off">
      <MainContent>
        <Grid
          container
          sx={{ height: '100%' }}
          alignItems="stretch"
          spacing={0}
        >
          <Hidden mdDown>

            <GridWrapper
              xs={12}
              md={6}
              alignItems="center"
              display="flex"
              className='login-bg'
              style={divStyle}
              justifyContent="center"
              item
            >


              <Container maxWidth="lg" >
                <Box textAlign="left">
                  <TypographyPrimary variant="h1" sx={{ my: 1}}
                  fontSize="40px"
                  fontWeight="lighter"
                  letterSpacing="1px">
                  Welcome to 
                  </TypographyPrimary>

                  <TypographyPrimary variant="h1" sx={{ my: 1 }}
                  fontSize="40px"
                  letterSpacing="1px"
                  >
                  <b>INABIA EBOT</b>
                  </TypographyPrimary>
                  <TypographySecondary
                    variant="h4"
                    fontWeight="normal"
                    sx={{ mb: 4 }}
                    color="#fff"
                  >

                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua
                  </TypographySecondary>

                </Box>
              </Container>

            </GridWrapper>

          </Hidden>

          <Grid
            xs={12}
            md={6}
            alignItems="center"
            display="flex"
            justifyContent="center"
            item
          >
            <Container maxWidth="sm">

              <Box textAlign="center">
                <img
                  alt="500"
                  height={80}
                  src="/static/images/inabia_ai_logo.png"
                />
                <br></br>
                <br></br>

                <Typography variant="h2" sx={{ my: 1 }}>

                <TextField
                      error={state.isError}
                      fullWidth
                      id="username"
                      type="email"
                      label="Username"
                      placeholder="Username"
                      margin="normal"
                      onChange={handleUsernameChange}
                      onKeyPress={handleKeyPress}
                      autoComplete="current-email"
                      sx={{ mb: 2 }}
                    />

                <br></br>

                <TextField
                      error={state.isError}
                      fullWidth
                      id="password"
                      type="password"
                      label="Password"
                      placeholder="Password"
                      margin="normal"
                      helperText={state.helperText}
                      onChange={handlePasswordChange}
                      onKeyPress={handleKeyPress}
                      autoComplete="current-password"
                      sx={{ mb: 2 }}
                    />

                </Typography>

                {/* <LoadingButton
                  onClick={handleClick}
                  loading={pending}
                  variant="outlined"
                  color="primary"
                  startIcon={<RefreshTwoToneIcon />}
                >
                  Refresh view
                </LoadingButton> */}
                <Button href="/register" 
                variant="contained"
                sx={{ mb: 2 }}
                className={classes.loginBtn}
                onClick={handleLogin}
                disabled={state.isButtonDisabled}
                >
                  Signin
                </Button>
                <Typography variant="h6" sx={{ my: 1 }}>
                  Don't have a account, <a href="register"
                  target="_blank" 
                  color="red" 
                  > register now</a>
                </Typography>
              </Box>
            </Container>
          </Grid>
        </Grid>
      </MainContent>
      </form>
    </>
  );
}

export default Status500;
